CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_checkLocationUpdate`(
     IN punit_number VARCHAR (25)
     , IN phouse_number VARCHAR (25)
     , IN pstreet VARCHAR (255)
     , IN psuburb VARCHAR (100)
     , IN date_time DATETIME
)
BEGIN
     DECLARE ret INT DEFAULT 0 ;
     DECLARE locationId INT DEFAULT 0 ;
     SELECT 
          id 
     FROM
          `location` 
     WHERE unit_number = punit_number 
          AND house_number = phouse_number 
          AND `street` LIKE pstreet 
          AND `suburb` LIKE psuburb 
     LIMIT 1 INTO locationId ;
     IF (locationId != 0) 
     THEN IF EXISTS 
     (SELECT 
          id 
     FROM
          `location` 
     WHERE id = locationId 
          AND `modified_ts` > date_time) 
     THEN -- update available
     SET @ret = 1 ;
     SELECT DISTINCT 
          @ret AS `status`
          , location.id
          , unit_number
          , house_number
          , street
	  , street_type
          , suburb
          , postcode
          , collection_day
	  , bin_type
	  , dwelling
          , `modified_ts` AS date_time
	  , (SELECT week_type FROM calendar WHERE CURDATE() BETWEEN STR_TO_DATE(week_commencing, '%e/%c/%Y') AND DATE_ADD(STR_TO_DATE(week_commencing, '%e/%c/%Y'), INTERVAL 6 DAY) LIMIT 1) AS current_week_type
     FROM
          `location` 
     WHERE `location`.id = locationId ;
     ELSE -- update not available
     SET @ret = 2 ;
     SELECT 
          @ret AS `status` ;
     END IF ;
     ELSE -- location doesn't exists
     SET @ret = 0 ;
     SELECT 
          @ret AS `status` ;
     END IF ;
END$$

