CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_getPagesById`(in pageId int)
BEGIN
SELECT `pages`.`id` AS page_id
	, `page_title`
	, `page_description`
	, `page_content`
	, `page_image`
	, `section_id`
	, `type_of_calendar`
	, `section`.`title` AS section
	, `latitude`
	, `longitude`
	, `order`
	, `page_status`
	, c.`allowed`
	, c.`not_allowed`
	, (CASE
	    section_id 
	    WHEN 2 
	    THEN w.bin_type_id 
	    WHEN 3 
	    THEN cb.bin_type_id 
	    ELSE NULL 
	  END  ) AS bin_type_id
	, (CASE
	    section_id 
	    WHEN 2 
	    THEN w.bin_color_id 
	    WHEN 3 
	    THEN cb.bin_color_id 
	    ELSE NULL 
	  END ) AS bin_color_id 
	FROM pages
	JOIN `section` ON `section`.`id` = `pages`.`section_id`
	  LEFT JOIN `calendar_bin_type` AS c 
	    ON `c`.`page_id` = `pages`.`id` 
	  LEFT JOIN `waste_bin_type` AS w 
	    ON `w`.`page_id` = `pages`.`id` 
	  LEFT JOIN `bin_type_color` AS cb 
	    ON `c`.`bin_type_color_id` = `cb`.`id` 
	where `pages`.`id` = pageId and `pages`.`page_status` = '1' ;
    END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_getPagesBySection`(IN sectionId INT,IN binType INT, IN calendar_other INT)
BEGIN
	DECLARE pQuery VARCHAR (5000);
	SET @pQuery = "select `pages`.`id` as page_id
	, `page_title`
	, `page_description`
	, `page_image`
	, `section_id`
	, `type_of_calendar`
	, `section`.`title` as section
	, `latitude`
	, `longitude`
	, `order`
	, `page_status`
	, c.`allowed`
	, c.`not_allowed`
	, (CASE
	    `section_id` 
	    WHEN 2 
	    THEN w.bin_type_id 
	    WHEN 3 
	    THEN IFNULL(cb.bin_type_id , c.bin_type)
	    ELSE null 
	  END  ) as bin_type_id
	, (CASE
	    `section_id` 
	    WHEN 2 
	    THEN w.bin_color_id 
	    WHEN 3 
	    THEN cb.bin_color_id 
	    ELSE null 
	  END ) as bin_color_id 
	from pages
	join `section` on `section`.`id` = `pages`.`section_id`
	  LEFT JOIN `calendar_bin_type` AS c 
	    ON `c`.`page_id` = `pages`.`id` 
	  LEFT JOIN `waste_bin_type` AS w 
	    ON `w`.`page_id` = `pages`.`id` 
	  LEFT JOIN `bin_type_color` AS cb 
	    ON `c`.`bin_type_color_id` = `cb`.`id` 
	WHERE `pages`.`page_status` = '1' AND `section_id` = ";
	SET @pQuery = CONCAT(@pQuery, sectionId );
	IF sectionId = 2 THEN
		IF binType > 0 THEN 
			SET @pQuery = CONCAT(@pQuery, " Having bin_type_id = 0 or bin_type_id = ",binType);	
		ELSEIF binType = 0 THEN
			SET @pQuery = CONCAT(@pQuery, " Having bin_type_id = 0  ");	
		END IF;
	ELSEIF sectionId = 3 THEN
		IF binType > 0 THEN 
			IF calendar_other = 1 THEN
				SET @pQuery = CONCAT(@pQuery, " Having bin_color_id IS NULL AND bin_type_id = ",binType);
			ELSE 
				SET @pQuery = CONCAT(@pQuery, " Having bin_type_id = ",binType);
			END IF;
		ELSEIF binType = 0 THEN
			SET @pQuery = CONCAT(@pQuery, " Having type_of_calendar = '2'   ");	
		END IF;
	END IF;
	SET @pQuery = CONCAT(@pQuery, " ORDER BY  `page_title`  "); -- IF(sectionId = 2, " ORDER BY  `page_title`  ", " ORDER BY  `order`  "));	
	PREPARE stmt FROM @pQuery ;
	EXECUTE stmt ;
	DEALLOCATE PREPARE stmt ;
    END$$

