CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_searchLocation`(
    IN stNumber VARCHAR(25),
    IN street VARCHAR (255),
    IN postCode INT,
    IN psuburb VARCHAR (255),
    IN plimit INT,
    IN poffset INT,
    IN splitValue TINYINT
    )
BEGIN
	DECLARE pQuery VARCHAR (1000);
	DECLARE delim VARCHAR(12);
	SET delim = ' ';
	SET @Valcount = substrCount(street,delim)+1;
	SET @v1=0;
	SET @pQuery = "SELECT sql_calc_found_rows DISTINCT
	  0 as unit_number,
	  house_number,
	  street,
	  street_type,
	  suburb,
	  postcode,
	  collection_day,
	  bin_type,
	  dwelling, 
	  '0000-00-00 00:00:00' AS date_time
	  , (SELECT week_type FROM calendar WHERE CURDATE() BETWEEN STR_TO_DATE(week_commencing, '%e/%c/%Y') AND DATE_ADD(STR_TO_DATE(week_commencing, '%e/%c/%Y'), INTERVAL 6 DAY) limit 1) as current_week_type
	  FROM `location`
	  WHERE (CONCAT_WS(' ', RTRIM(street), RTRIM(street_type)) LIKE ";
	SET @pQuery = CONCAT(@pQuery," '%", street ,"%' ");
	IF splitValue = 1 THEN 
		WHILE (@v1 < @Valcount) DO
			SET @val = stringSplit(street,delim,@v1+1);
			SET @pQuery = CONCAT(@pQuery, " OR `street` LIKE '%", @val ,"%' ");
			SET @v1 = @v1 + 1;
		END WHILE;
			-- SET @pQuery = CONCAT(@pQuery, " AND house_number = ",stNumber);	
	END IF;
	SET @pQuery = CONCAT(@pQuery," )");	
	IF stNumber > 0 THEN 
		SET @pQuery = CONCAT(@pQuery, " AND house_number = ",stNumber);	
	END IF;
	IF postCode > 0 THEN 
		SET @pQuery = CONCAT(@pQuery, " AND postcode like '%",postCode,"%'");	
	END IF;
	SET @pQuery = CONCAT(@pQuery, " AND `suburb` LIKE '", psuburb , "'  AND `deleted` != 1 ");
	SET @pQuery = CONCAT(@pQuery, " GROUP BY `location`.id ");
	SET @pQuery = CONCAT(@pQuery, " ORDER BY suburb, street, street_type, house_number ");
	SET @pQuery = CONCAT(@pQuery, " LIMIT ",poffset, ', ',plimit);
	PREPARE stmt FROM @pQuery ;
	EXECUTE stmt ;
	DEALLOCATE PREPARE stmt ;
	/*
	if (row_count() = 0 and splitValue = 0) then
		CALL sp_searchLocation(stNumber,street,postCode,psuburb,plimit, poffset, 1);
	end if;
	*/
    END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_setDevice`(
     IN puuid VARCHAR (100)
     , IN token TEXT
     , IN deviceType TINYINT
     , IN uNumber VARCHAR (25)
     , IN hNumber VARCHAR (25)
     , IN stAddress VARCHAR (255)
     , IN psuburb VARCHAR (255)
     , IN ppostcode VARCHAR (255)
     , IN insert_add INT
     , IN deviceEnv TINYINT
)
BEGIN
     INSERT INTO `device_info` (
          `device_uuid`
          , `device_token`
          , `device_type`
          , `unit_number`
          , `house_number`
          , `street`
          , `suburb`
          , `postcode`
          , `device_env`
     ) 
     VALUES
          (puuid, token, deviceType, uNumber, hNumber, stAddress, psuburb, postcode, deviceEnv  ) 
          ON DUPLICATE KEY 
          UPDATE 
               `device_token` = IF(insert_add = 0, token, device_token)
               , `unit_number` = IF(insert_add = 1, uNumber, unit_number) 
               , `house_number` = IF(insert_add = 1, hNumber, house_number) 
               , `street` = IF(insert_add = 1, stAddress, street) 
               , `suburb` = IF(insert_add = 1, psuburb, suburb)
               , `postcode` = IF(insert_add = 1, ppostcode, postcode)
               , `device_env` = IF(insert_add = 0, deviceEnv, device_env)  ;
     SELECT 
          1 AS STATUS ;
END$$

