(6, 'Aerosol cans ', '', '<p><span style="font-family:arial;"><span style="font-size:14px;">Empty aerosol cans are 100% recyclable.&nbsp;</span></span></p>\n\n<p><span style="font-family:arial;"><span style="font-size: 14px; line-height: 1.6;">Please place empty aerosol cans into your recycling bin.&nbsp;</span></span></p>\n\n<p><span style="font-family:arial;"><span style="font-size:14px;">Please remove the plastic cap and place this in your red bin.</span></span></p>\n', '50d7c25fe41215d602aa30cdfc54bdb4.jpg', 2, '0', '', '', 0, '2015-04-01 15:25:50', '2015-10-21 15:43:35', NULL, NULL, '1'),
(7, 'Aluminium cans', '', '<p><span style="font-family:arial;"><span style="font-size:14px;">Aluminium cans are recyclable - please place them into your recycling bin. &nbsp;</span></span></p>\n\n<p><span style="font-family:arial;"><span style="font-size:14px;">It is best to squash cans before placing them in the recycling bin to reduce the amount of space they take up.&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;<br />\nAluminium cans can also be recycled at scrap metal recyclers. &nbsp; &nbsp;</span></span></p>\n\n<p>&nbsp;</p>\n', '39e9b89a7c7a68a44d8d74f7e1e8e14a.jpg', 2, '0', '', '', 0, '2015-04-01 15:27:44', '2015-10-21 15:45:29', NULL, NULL, '1'),
(8, 'Asbestos', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Asbestos can <strong>NEVER </strong>be disposed of in your household bins, street litter bins, skip bins, council clean-ups, alongside roads or down drains. Asbestos can <strong>ONLY</strong> be disposed of at licensed facilities&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;<br />\n<strong>There is NO safe level of exposure to asbestos fibres.</strong> &nbsp;&nbsp; &nbsp;<br />\nIf you are removing asbestos yourself you must observe safety precautions, otherwise you risk exposing yourself and your family to long-term health risks.&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;<br />\nFor any questions email:&nbsp;<a href="mailto:wsrads@penrithcity.nsw.gov.au">wsrads@penrithcity.nsw.gov.au</a>&nbsp;or call <a href="tel:02 4732 8084">02 4732 8084</a></span></span>&nbsp;&nbsp; &nbsp;</p>\n\n<p>&nbsp;</p>\n', '835b4f7c18a859b51bec14ed0c8c7048.png', 2, '0', '', '', 0, '2015-04-01 15:40:53', '2015-06-04 14:21:32', NULL, NULL, '1'),
(9, 'Batteries (car)', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Car batteries cannot be placed in your garbage or recycling bin.&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;<br />\nSITA Australia accepts car batteries for recycling at the Eastern Creek landfill at Wallgrove Road.</span></span><span style="font-size:14px;"><span style="font-family:arial;"> For more information call <a href="tel:1300 651 116">1300 651 116</a> or visit <a href="http://www.sita.com.au/">Sita Australia&#39;s Website</a>. &nbsp;&nbsp;&nbsp; &nbsp;</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Battery drop off is also available at Chemical CleanOut Events.</span></span></p>\n\n<p><br />\n<span style="font-size:14px;"><span style="font-family:arial;">Council hosts a Chemical CleanOut Event once a year where you can drop off both car and household batteries for free.&nbsp;&nbsp; &nbsp;</span></span></p>\n\n<p><br />\n<span style="font-size:14px;"><span style="font-family:arial;">For more information visit <a href="https://www.penrithcity.nsw.gov.au/Waste-and-Environment/Waste/Chemical-drop-off-days/">Chemical CleanOut Events</a> or call the Environment Line on <a href="tel:131 555">131 555</a>&nbsp;&nbsp; &nbsp;</span></span></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n', '57deebfab015abc8a4a29faeb3369ed9.jpg', 2, '0', '', '', 0, '2015-04-01 15:57:33', '2015-11-02 11:16:41', NULL, NULL, '1'),
(10, 'Aerosol Cans  ', '', '<p><span style="font-size:14px;"><span style="font-family:arial;"><span style="line-height: 1.6;">Empty Aerosol cans are 100% recyclable. &nbsp; &nbsp;</span></span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;"><span style="line-height: 1.6;">P</span><span style="line-height: 1.6;">lease place empty aerosol cans into your recycling bin.</span></span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Please remove the plastic cap and place this in your red bin</span></span></p>\n\n<p>&nbsp;</p>\n', 'ea3bf6202cca1960ca8080698684977b.jpg', 2, '0', '', '', 0, '2015-04-01 15:58:38', '2015-05-27 11:26:24', NULL, NULL, '0'),
(11, 'Books', '', '<p class="p1"><span style="font-size:14px;"><span style="font-family:arial;"><span class="s1">Books, if in good condition, can be donated to charities, sold or given away, saving it from landfill.</span></span></span></p>\n\n<p><span style="font-family:arial;"><span style="font-size:14px;">Unusable books can be recycled by placing them into your recycling bin.&nbsp;<br />\nPlease remove any metal bindings or plastic coatings before recycling.&nbsp;</span></span></p>\n\n<p><span style="font-family:arial;"><span style="font-size:14px;">Books can also be boxed in small quanities and removed as part of a Council-Clean Up&nbsp;</span></span></p>\n', '109577835702d5807ee89d249851ebdb.jpg', 2, '0', '', '', 0, '2015-04-01 16:21:30', '2015-05-27 11:22:13', NULL, NULL, '1'),
(12, 'Plastic Bottle Tops', '', '<p class="p1"><span style="font-size:14px;"><span style="font-family:arial;">Plastic bottle tops/lids can be recycled as long as they are still attached / screwed onto the bottle.&nbsp;&nbsp; &nbsp;<br />\nIf you squeeze the air out of the bottle, then replace the lid, you will save room in your recycling bin and both the bottle and top will be recycled.&nbsp;&nbsp; &nbsp;</span></span></p>\n\n<p class="p1">&nbsp;</p>\n', 'b4e878634043b096f8fc1d905ce1a61d.jpg', 2, '0', '', '', 0, '2015-04-01 16:25:49', '2015-05-21 13:03:15', NULL, NULL, '0'),
(13, 'Bottle tops ', '', '<p><span style="font-size:14px;"><span style="font-family:arial;"><strong>Metal tops&nbsp;</strong><br />\nMetal lids &amp; tops can be recycled in your recycling bin at home.&nbsp;</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;"><strong>Plastic tops&nbsp;</strong><br />\nPlastic bottle tops/lids can be recycled as long as they are still attached / screwed onto the bottle.&nbsp;<br />\nIf you squeeze the air out of the bottle, then replace the lid, you will save room in your recycling bin and both the bottle and top will be recycled.&nbsp;</span></span></p>\n\n<p class="p1">&nbsp;</p>\n', '81e991f4f0fcf64e379fa245a1192563.jpg', 2, '0', '', '', 0, '2015-04-01 16:30:52', '2015-11-02 09:23:18', NULL, NULL, '1'),
(14, 'Cards (greeting)', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Greeting cards, such as birthday or Christmas cards, that are made from paper or cardboard can go into your recycling bin.&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; </span></span>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</p>\n\n<p>&nbsp;</p>\n', '38355d51040049507e18dbd35e3bb3c7.jpg', 2, '0', '', '', 0, '2015-04-01 16:33:11', '2015-10-27 15:00:30', NULL, NULL, '1'),
(15, 'Cartons ', '', '<p class="p1"><span style="font-size:14px;"><span style="font-family:arial;">Cartons can be recycled in your yellow-lidded recycling bin at home.&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\nThis includes items such as: &nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; </span></span></p>\n\n<ul>\n	<li class="p1"><span style="font-size:14px;"><span style="font-family:arial;">Milk cartons&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span></span></li>\n	<li class="p1"><span style="font-size:14px;"><span style="font-family:arial;">Cream cartons&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span></span></li>\n	<li class="p1"><span style="font-size:14px;"><span style="font-family:arial;">Juice cartons&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span></span></li>\n</ul>\n\n<p class="p1"><span style="font-size:14px;"><span style="font-family:arial;">Squashing cartons can reduce the amount of space they take up, giving you more room in your bin.</span></span></p>\n\n<p class="p1"><br />\n<span style="font-size:14px;"><span style="font-family:arial;">It is important that no food or liquid is inside the cartons when being placed in the recycling bin. &nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; </span></span>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;</p>\n\n<p class="p1">&nbsp;</p>\n', 'ff7965e78cdd777456a70097b5532c15.jpg', 2, '0', '', '', 0, '2015-04-01 16:35:05', '2015-10-28 11:59:38', NULL, NULL, '1'),
(16, 'Batteries (household)', '', '<p>Household batteries can be recycled through dedicated battery recycling drop off points at the following locations:</p>\n\n<ul>\n	<li>Penrith Library</li>\n	<li>St Mary&#39;s Libary</li>\n	<li>St Clair Library</li>\n	<li>St Clair Leisure Centre</li>\n</ul>\n\n<p>Please DO NOT place batteries in your recycling bin as they cannot be recycled at the facility.</p>\n\n<p>Battery drop off is also available at Chemical CleanOut Events.</p>\n\n<p>Council hosts a Chemical drop-off day once a year where you can drop off both car and household batteries for free. For more information visit <a href="https://www.penrithcity.nsw.gov.au/Waste-and-Environment/Waste/Chemical-drop-off-days/">Chemical CleanOut Events website</a> or call the Environment Line on <a href="tel:131 555">131 555</a>.</p>\n', '926b10770bb6fa128db244de901cce6e.jpg', 2, '0', '', '', 0, '2015-04-01 17:24:56', '2015-11-30 14:45:06', NULL, NULL, '1'),
(17, 'Bicycles', '', '<p><span style="font-family:arial;"><span style="font-size:14px;">SITA Australia at Eastern Creek accepts bicycles free of charge.&nbsp;&nbsp; &nbsp;<br />\nThey are rebuilt then sold with proceeds going to charity.&nbsp;&nbsp; &nbsp;<br />\nContact SITA on <a href="tel:1300 651 116">1300 651 116</a> or visit <a href="http://www.sita.com.au/">Sita Australia&#39;s Website</a>&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;<br />\nBicycles can also be collected as part of a Council Clean-Up Collection.&nbsp;&nbsp; &nbsp;<br />\nFor more information see<a href="https://www.penrithcity.nsw.gov.au/Waste-and-Environment/Waste/On-call-clean-up/"> Council Clean-Up Collections</a> or call the Waste Hotline on <a href="tel:1800 734 735">1800 734 735</a>&nbsp;&nbsp; &nbsp;</span></span></p>\n\n<p>&nbsp;</p>\n', '299424d045cd14851506267a443bea2d.jpg', 2, '0', '', '', 0, '2015-04-01 19:35:13', '2015-05-21 19:09:59', NULL, NULL, '1'),
(18, 'Books ', '', '<p>Books, if in good condition, can be donated to charities, sold or given away, saving it from landfill.&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;<br />\nUnusable books can be recycled by placing them into your recycling bin.&nbsp;&nbsp; &nbsp;<br />\nPlease remove any metal bindings or plastic coatings before recycling.&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;<br />\nBooks can also be boxed in small quantities and removed as part of a Council-Clean Up&nbsp;&nbsp; &nbsp;<br />\nFor more information see Council Clean-Up Collections or call the Waste Hotline on 1800 734 735. &nbsp; &nbsp;</p>\n\n<p>&nbsp;</p>\n', '30c3c0d0cefee8ea5573901b2aa51634.jpg', 2, '0', '', '', 0, '2015-04-01 19:37:56', '2015-05-27 11:22:21', NULL, NULL, '0'),
(19, 'Plastic Bottle Tops', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Plastic bottle tops/ lids from drink bottles, milk bottles and detergent bottles&nbsp;CANNOT be recycled in your recycling bin-&nbsp;even if they are marked with a plastic identification code 1, 2, 3, 4, 5, 6, or 7.&nbsp; They are too small to be picked up in the recycling sorting process and cause hazard to the sorting workers if they are left on the bottles.&nbsp; Please place plastic bottle lids into the garbage bin.</span></span></p>\n', '1fb10c9046197a09d0a73ec094bcbed5.jpg', 2, '0', '', '', 0, '2015-04-01 20:21:33', '2015-05-21 13:03:40', NULL, NULL, '0'),
(20, 'Glass/ Metal Bottle Tops', '', '<p><span style="font-size:14px;"><span style="font-family:arial;"><strong>Metal tops&nbsp;</strong><br />\nMetal lids &amp; tops can be recycled in your recycling bin at home.&nbsp;</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;"><strong>Plastic tops&nbsp;</strong><br />\nPlastic bottle tops/lids can be recycled as long as they are still attached / screwed onto the bottle.&nbsp;<br />\nIf you squeeze the air out of the bottle, then replace the lid, you will save room in your recycling bin and both the bottle and top will be recycled.&nbsp;</span></span></p>\n', '5ab08d811bae6ddfbd596327c358a2d2.jpg', 2, '0', '', '', 0, '2015-04-01 20:24:09', '2015-05-27 11:34:25', NULL, NULL, '0'),
(21, 'Bricks', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Waste from construction &amp; development accounts for approximately 33% of all waste deposited annually in landfill.&nbsp;&nbsp; &nbsp;<br />\nOf this material, 75% can be recycled and reused.&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;<br />\nPlease do not place bricks in your recycling bin as it cannot be recycled at the facility.&nbsp;&nbsp; &nbsp;<br />\nPlease do not fill up your red bin with bricks as it can make your bin too heavy to empty.&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;<br />\nFor a list of businesses that may accept bricks for recycling, visit PlanetArk - Construction &amp; Demolition Recycling&nbsp;&nbsp; &nbsp;<br />\n<a href="http://recyclingnearyou.com.au/">http://recyclingnearyou.com.au/</a> or call <a href="tel:1300733712">1300 733 712</a></span></span></p>\n\n<p>&nbsp;</p>\n', '919327888427c194761e7e1964e8cddd.jpg', 2, '0', '', '', 0, '2015-04-01 20:25:24', '2015-06-04 14:25:15', NULL, NULL, '1'),
(22, 'Building materials', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Building materials cannot be disposed of in any of the household bins.&nbsp;&nbsp; &nbsp;<br />\nThey pose a risk to the safety of workers, machinery and often make the bin too heavy to collect.&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;<br />\nFor information on specific items, please see;&nbsp;&nbsp; &nbsp;</span></span></p>\n\n<ul>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Asbestos&nbsp;&nbsp; &nbsp;</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Bricks&nbsp;&nbsp; &nbsp;</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Concrete&nbsp;&nbsp; &nbsp;</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Paint &amp; paint tins&nbsp;&nbsp; &nbsp;</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Tiles&nbsp;&nbsp; &nbsp;</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Timber&nbsp;&nbsp; &nbsp;</span></span></li>\n</ul>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">&nbsp;&nbsp; &nbsp;<br />\nFor a list of businesses that may accept building materials for recycling, visit PlanetArk - Construction &amp; Demolition Recycling&nbsp;&nbsp;<a href="http://recyclingnearyou.com.au/">http://recyclingnearyou.com.au/</a> &nbsp;or call <a href="tel:1300 733 712">1300 733 712</a></span></span></p>\n\n<p>&nbsp;</p>\n', '8343e97b63235cf1f366aff4362d2cc8.jpg', 2, '0', '', '', 0, '2015-04-01 20:27:14', '2015-11-02 09:23:38', NULL, NULL, '1'),
(23, 'Car Batteries', '', '<p><a href="http://localhost/9">See Batteries</a></p>', '6824ae17c6787b01633d811c03158321.jpg', 2, '0', '', '', 0, '2015-04-01 20:29:11', '2015-10-27 14:57:46', NULL, NULL, '0'),
(24, 'Car parts & bodies', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Car parts &amp; bodies cannot be disposed of in any of the household bins.&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\nThey pose a risk to the safety of workers &amp; machinery.&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\nCar pars &amp; bodies can be disposed of (for a fee) at:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\nSita Australia Landfill - Wallgrove Road, Eastern Creek&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\nFor more information call <a href="tel:1300 651 116">1300 651 116</a> or visit <a href="http://www.sita.com.au/">Sita Australia&#39;s Website</a>&nbsp;&nbsp;</span></span> &nbsp;&nbsp;&nbsp; &nbsp;</p>\n\n<p>&nbsp;</p>\n', '227a0f3189890dfb24b2ea41689130db.jpg', 2, '0', '', '', 0, '2015-04-01 20:31:31', '2015-11-02 09:24:25', NULL, NULL, '1'),
(25, 'Car (tyres)', '', '<p><span style="font-size:14px;"><span style="font-family:arial;"><a href="http://localhost/101">See Tyres</a></span></span></p>\n', '9fec0e9ac668238b58b319f61d89b452.png', 2, '0', '', '', 0, '2015-04-01 20:40:13', '2015-11-02 09:23:55', NULL, NULL, '1'),
(26, 'Cardboard', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Cardboard can be recycled in your yellow-lidded recycling bin at home.&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<br />\nThis includes items such as: &nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; </span></span></p>\n\n<ul>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Tissue boxes&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Egg cartons&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Cereal boxes&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Moving boxes&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span></span></li>\n</ul>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Squashing boxes and cartons can reduce the amount of space they take up, giving you more room in your bin. It is important that no food is inside the cartons when being placed in the recycling bin. &nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />\nFor 3 bin services, clean cardboard (cardboard that does not have plastic tape, plastic coatings, staples or plastic windows) that has food stuck to it, such as pizza boxes, can be placed in your organics bin. &nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;<br />\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\nBulky or excess cardboard and paper can be recycled at Cardboard King - 8 Kommer Place St Marys - Contact: <a href="tel:9673 1660">9673 1660</a> or visit <a href="http://www.cardboardking.com.au/">Cardboard King&#39;s website</a>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;</span> &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span></p>\n\n<p>&nbsp;</p>\n', '03ae913f33b502fbc41d951362dba8ae.jpg', 2, '0', '', '', 0, '2015-04-01 20:43:52', '2015-10-28 11:58:40', NULL, NULL, '1'),
(27, 'Cards (Greeting)', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Birthday, Christmas, Get Well and other greeting cards made of paper or cardboard can be recycled in your recycling bin at home.</span></span></p>\n', 'cb99a2d902a21ada63038fe7e82d89ba.jpg', 2, '0', '', '', 0, '2015-04-01 20:44:53', '2015-05-27 11:32:06', NULL, NULL, '0'),
(28, 'Cartons', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Milk and juice cartons can be put into your recycling bin. &nbsp;These are made from &#39;liquidpaperboard&#39; which is cardboard coated on both sides with plastic so it is watertight. Liquidpaperboard cartons that had milk, cream, sour cream, custard, pet milk, or juice can all be recycled.&nbsp;</span></span></p>\n', '8e9746c4c51026285d1d0699f060a2e1.jpg', 2, '0', '', '', 0, '2015-04-01 20:56:33', '2015-05-27 11:24:47', NULL, NULL, '0'),
(29, 'Ink cartridges ', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Ink cartridges can be recycled through dedicated drop off points.&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />\nFor recycling locations near you, visit Planet Ark - Ink Cartridges Recycling&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n<a href="http://recyclingnearyou.com.au/">http://recyclingnearyou.com.au/</a> &nbsp;or call <a href="tel:1300733712">1300 733 712</a>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span></span></p>\n\n<p>&nbsp;</p>\n', '17e32b833711cef66ff1f4d1be76a215.png', 2, '0', '', '', 0, '2015-04-01 21:00:02', '2015-11-02 09:30:18', NULL, NULL, '1'),
(30, 'Chemicals (Agriculture & Veterinary)', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">For more information on how to safely dispose of agricultural and veterinary-related chemicals, visit <a href="http://agsafe.com.au/">Agsafe Australia</a>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; </span></span>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</p>\n\n<p>&nbsp;</p>\n', '2d8e94b1acbf4a6c4d810bc0639f76ab.jpg', 2, '0', '', '', 0, '2015-04-01 21:02:05', '2015-05-21 18:50:10', NULL, NULL, '0'),
(31, 'Chemicals ', '', '<p><span style="font-size:14px;"><span style="font-family:arial;"><strong><span style="line-height: 1.6;">Household chemicals:&nbsp;</span></strong></span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Chemicals cannot be disposed of in any of the household bins.&nbsp;<br />\nThey pose a risk to the safety of workers and your household.&nbsp;</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;"><strong>Household chemicals can be dropped off for free at Council&#39;s Chemical CleanOut Events.&nbsp;</strong></span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">This includes chemicals such as:</span></span></p>\n\n<ul>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Acids and alkalis</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Ammonia-based cleaners</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Car and household batteries</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Fire extinguishers</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Floor care products</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Gas bottles</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Hobby chemicals</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Motor oils and cooking oils</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Paint and paint-related products</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Pesticides and herbicides</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Poisons</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Pool chemicals</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Smoke detectors</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Fluorescent globes and tubes</span></span></li>\n</ul>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">For more information, visit <a href="https://www.penrithcity.nsw.gov.au/Waste-and-Environment/Waste/Chemical-drop-off-days/"><u>Chemical CleanOut Events</u> </a>or call the Environment Line on <a href="tel:131 555">131 555&nbsp;</a></span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">To receive a FREE text message reminder about Chemical CleanOut Events as well as Electronic waste drop-off days and other waste and recycling initiatives, register for Council&#39;s Resource Recovery Reminder.&nbsp;</span></span></p>\n\n<p><br />\n<span style="font-size:14px;"><span style="font-family:arial;">Send your name, home address and mobile phone number to <a href="mailto:rrr@penrithcity.nsw.gov.au">rrr@penrithcity.nsw.gov.au</a> or call Waste Services on <a href="tel:4732 7615">4732 7615</a> to register for this free service.&nbsp;</span></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;"><strong>Agricultural, Veterinary Chemicals:</strong></span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">For more information on how to safely dispose of agricultural and veterinary-related chemicals, visit <a href="http://agsafe.com.au/">Agsafe Australia</a>&nbsp;</span></span></p>\n', 'ef1fef44fad4939ff0a87342091b77ac.jpg', 2, '0', '', '', 0, '2015-04-01 21:04:52', '2015-11-02 11:19:46', NULL, NULL, '1'),
(32, 'Clothing', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">If in bad condition, clothing can be placed into your red-lidded residual waste bin.&nbsp;&nbsp;</span></span> &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n<span style="font-size:14px;"><span style="font-family:arial;">Clothing can also be boxed or bagged and removed as part of a Council-Clean Up. For more information see <a href="https://www.penrithcity.nsw.gov.au/Waste-and-Environment/Waste/On-call-clean-up/">Council Clean-Up Collections</a> or call the Waste Hotline on <a href="tel:1800 734 735">1800 734 735</a>.</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Clothing, if in good condition, can be donated to charities, sold or given away, saving it from landfill.</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;<br />\nClothing donation points (clothing bins) are located in many shopping centres and other public areas.&nbsp;&nbsp; </span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\nPlease ensure any donations are in a re-usable / sellable condition and are placed entirely within the donation bin.&nbsp;&nbsp;</span></span> &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</p>\n\n<p>&nbsp;</p>\n', '9e62f1a3dd7aee61bb8582f0624e6544.jpg', 2, '0', '', '', 0, '2015-04-01 21:06:16', '2015-10-28 12:08:40', NULL, NULL, '1'),
(33, 'Community sharps', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">&quot;Community Sharps&quot; are needles, syringes, lancets, cannulas and other equipment resulting from medical care, animal health care or drug use.&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n<strong>All sharps should always be kept secure and separate from other wastes.</strong>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\nSharps pose a high risk to workers and community members and should <strong>NEVER</strong> be disposed of in your household bins, street bins, toilet bins or toilets or down drains.&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n<strong>Safe sharp drop-off bins are located at:&nbsp;&nbsp; &nbsp;</strong>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span></span></p>\n\n<ul>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Penrith Community Health Centre - Soper Place PENRITH (24 hours)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">St Marys Community Sharps Bin - East Lane ST MARYS (24 hours)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Nepean Hospital - Derby Street KINGSWOOD (Near South Court Primary Care entrance) (24 hours)&nbsp;</span></span></li>\n</ul>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">&nbsp;&nbsp;</span></span></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n', '5be133876cfb7cc5fc27c8603033fb05.jpg', 2, '0', '', '', 0, '2015-04-01 21:20:16', '2015-11-02 09:39:12', NULL, NULL, '0'),
