(94, 'Smoke alarms', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Most Smoke Alarms are labelled &ldquo;Warning-Radioactive Material&rdquo; and also &ldquo;Do not dispose of with general rubbish&rdquo;. However, the Australian Radiation Protection and Nuclear Safety Agency advises that it is safe to dispose of old smoke alarms in small quantities in the red-lidded residual waste bin, as the levels of radiation are considered small enough to be safe.&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />\nSmoke alarms &amp; detectors can be dropped off for free at Council&#39;s Chemical drop-off days.&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\nFor more information visit <a href="https://www.penrithcity.nsw.gov.au/Waste-and-Environment/Waste/Chemical-drop-off-days/">Chemical drop off days</a> or call the Environment Line on <a href="tel:131 555">131 555</a>. &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;<br />\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; </span></span>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</p>\n\n<p>&nbsp;</p>\n', '9d1f84d2483a0bd5aadb7f914343fbb4.jpg', 2, '0', '', '', 0, '2015-04-02 22:25:56', '2015-11-02 09:38:37', NULL, NULL, '1'),
(95, 'Sump Oil', '', '<p><span style="font-size:14px;"><span style="font-family:arial;"><a href="https://www.penrithcity.nsw.gov.au/Waste-and-Environment/Waste/A-Z-of-waste-and-recycling/#O>see&nbsp;Oil - (Motor)</a></span></span></p>', '2da543336a56e8d0f66848e75115251e.png', 2, '0', '', '', 0, '2015-04-02 22:27:05', '2015-05-27 13:32:40', NULL, NULL, '0'),
(96, 'Syringes', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Syringes are known as &quot;Community Sharps&quot; which includes needles, syringes, lancets, cannulas and other equipment resulting from medical care, animal health care or drug use.&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />\n<strong>All sharps should always be kept secure and separate from other wastes.&nbsp;&nbsp; &nbsp;&nbsp;</strong>&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<br />\nSharps pose a high risk to workers and community members and should <strong>NEVER </strong>be disposed of in your household bins, street bins, toilet bins or toilets or down drains.&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n<strong>Safe sharp drop-off bins are located at:</strong>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span></span></p>\n\n<ul>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Penrith Community Health Centre - Soper Place PENRITH (24 hours)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">St Marys Community Sharps Bin - East Lane ST MARYS (24 hours)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<span style="font-size:14px;"><span style="font-family:arial;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Nepean Hospital - Derby Street KINGSWOOD (Near South Court Primary Care entrance) (24 hours)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span></span></li>\n</ul>\n\n<p>&nbsp;</p>\n', 'a8ddd72f7850e480c01acbaa95461ef1.png', 2, '0', '', '', 0, '2015-04-02 22:27:55', '2015-05-27 13:34:26', NULL, NULL, '1'),
(97, 'Televisions', '', '<p><span style="font-family: arial; font-size: 14px; line-height: 22.3999996185303px;">Televisions in good working conditions can also be sold or given away, saving it from landfill.&nbsp;&nbsp;</span><span style="font-family: arial; font-size: 14px; line-height: 22.3999996185303px;">&nbsp;</span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Televisions can also be recycled through Council&#39;s E-Waste drop off days.&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\nFor more information visit <a href="https://www.penrithcity.nsw.gov.au/Waste-and-Environment/Waste/E-Waste-drop-off-days/">E-Waste drop off days</a> or call Waste Services on <a href="tel:4732 7615">4732 7615</a>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></p>\n\n<p>&nbsp;</p>\n', 'd08830d0452b22860aee702e039e4ea3.png', 2, '0', '', '', 0, '2015-04-02 22:32:23', '2015-05-27 13:35:54', NULL, NULL, '1'),
(98, 'Tiles', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Tiles can be safely disposed of at:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</span></span></p>\n\n<ul>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Hallinans Haulage Recycling Centre - 37-55 Lee Holm Rd, St Marys - 9826 1666</span></span>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; <span style="font-size:14px;"><span style="font-family:arial;">&nbsp; &nbsp; &nbsp;&nbsp;</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Sita Australia - Wallgrove Rd, Eastern Creek (Charges may apply) . &nbsp;For mo</span></span><span style="font-family: arial; font-size: 14px; line-height: 1.6;">re information call <a href="tel:1300 651 116">1300 651 116</a> or visit Sita Australia&#39;s Website at:</span><span style="font-size:14px;"><a href="http://www.sita.com.au/"><span style="font-family:arial;">http://www.sita.com.au/</span></a><span style="font-family: arial; line-height: 1.6;"> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></li>\n</ul>\n\n<p>&nbsp;</p>\n', 'f0d80c11ddd7c18e0eee7743f3f8500f.png', 2, '0', '', '', 0, '2015-04-02 22:34:26', '2015-05-27 13:36:41', NULL, NULL, '1'),
(99, 'Timber', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Both treated and untreated timber is accepted at:&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\nSita Australia - Wallgrove Rd, Eastern Creek (Charges apply)&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\nFor more information call <a href="tel:1300 651 116">1300 651 116</a> or visit Sita Australia&#39;s Website at <a href="http://www.sita.com.au">www.sita.com.au</a></span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">&nbsp; &nbsp; &nbsp; &nbsp;</span></span></p>\n\n<p>&nbsp;</p>\n', 'b409aac54161ad1805c59854d9535ccc.jpg', 2, '0', '', '', 0, '2015-04-02 22:40:27', '2015-05-27 13:37:19', NULL, NULL, '1'),
(100, 'Toys', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Toys in bad condition can be placed into the red-lidded residual waste bin.</span></span></p>\n\n<p><span style="font-family: arial; font-size: 14px; line-height: 22.3999996185303px;">If in good condition, toys can be donated to charities, sold or given away, saving it from landfill. &nbsp;</span><span style="font-family: arial; font-size: 14px; line-height: 1.6;">&nbsp;</span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Toys can also be boxed or bagged and removed as part of a Council-Clean Up&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\nFor more information see <a href="https://www.penrithcity.nsw.gov.au/Waste-and-Environment/Waste/On-call-clean-up/">Council Clean-Up Collections</a> or call the Waste Hotline on <a href="tel:1800 734 735">1800 734 735</a>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></span></p>\n\n<p>&nbsp;</p>\n', '17d32f15bed10b0563d08516abd874e6.jpg', 2, '0', '', '', 0, '2015-04-02 22:42:06', '2015-05-27 13:38:33', NULL, NULL, '1'),
(101, 'Tyres', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Tyres can be recycled into many new products.&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\nVarious uses of recycled tyres include the use in retreadable casing, tyre derived fuel in cement industry, or use in constructions of speed bumps and playground equipment.&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\nFor a list of businesses &amp; organisations that may accept tyres for recycling, visit PlanetArk - Tyres&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n<a href="http://recyclingnearyou.com.au/">http://recyclingnearyou.com.au/</a> or call <a href="tel:1300733712">1300 733 712</a>&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;</span></span>&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;</p>\n\n<p>&nbsp;</p>\n', '59c7e64d548c0161cb6b1978dd399124.png', 2, '0', '', '', 0, '2015-04-02 22:44:17', '2015-06-04 14:52:18', NULL, NULL, '1'),
(102, 'Tyres', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Tyres can be recycled into many new products<br />\nTyrecycle -&nbsp;81-85 Christie St, St Marys, ph <a href="tel:9623 9880">9623 9880</a><br />\nKmart Tyre &amp; Auto Service Penrith- free tyre recycling with tyre purchase, Centro Nepean, Cnr Station St &amp; Woodriff St, Penrith, ph <a href="tel:4721 1971">4721 1971</a><br />\nKmart Tyre &amp; Auto Service, Coles Express St Clair- free tyre recycling with tyre purchase, Cnr Mamre Rd &amp; Banks Dr, St Clair, ph <a href="tel:9670 5207">9670 5207</a><br />\nTyres are shredded for reuse as second hand tyres and retreadable casings, or for use in the cement industry as tyre derived fuel for energy recovery or granulated as feed stick material for the production of rubber crumb or shredded for civil engineering applications such as making speed bumps or playground equipment.</span></span></p>\n', '', 2, '0', '', '', 0, '2015-04-06 13:49:54', '2015-05-22 14:04:44', NULL, NULL, '0'),
(103, 'Whitegoods', '', '<p><span style="font-size: 14px;"><span style="font-family: arial;">Whitegoods in a usable condition could also be sold or given away.</span></span></p>\n\n<p><span style="font-family: arial; font-size: 14px; line-height: 1.6;">Many whitegoods contain valuable metals and resources which can be recycled for use in new products. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">For a list of businesses &amp; organisations that may accept whitegoods for recycling, visit PlanetArk - Whitegoods <a href="http://www.recyclingnearyou.com.au/">http://www.recyclingnearyou.com.au/</a> or call <a href="tel:1300 733 712">1300 733 712</a>. &nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;<br />\nMost whitegoods can be removed as part of a Council-Clean Up. For more information see <a href="https://www.penrithcity.nsw.gov.au/Waste-and-Environment/Waste/On-call-clean-up/">Council Clean-Up Collections</a> or call the Waste Ho</span></span>tline on<span style="font-size:14px;"><span style="font-family:arial;"> <a href="tel:1800 734 735">1800 734 735</a>.&nbsp; </span></span>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;<br />\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\n\n<p>&nbsp;</p>\n', '96fee273051f799e04800a580f6bd39a.jpg', 2, '0', '', '', 0, '2015-04-06 13:52:13', '2015-10-29 09:51:20', NULL, NULL, '1');
INSERT INTO `pages` (`id`, `page_title`, `page_description`, `page_content`, `page_image`, `section_id`, `type_of_calendar`, `latitude`, `longitude`, `order`, `created_ts`, `modified_ts`, `created_by`, `modified_by`, `page_status`) VALUES
(104, 'X-Rays', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">X-Rays contain silver in the form of halides, which can be extracted and converted into pure silver. The x-ray films are heated using temperatures of over 1,000 degrees celsius, the silver is extracted and converted into a more pure silver form. The silver can then be reused for things such as silver solder, jewellery, the silver plating of utensils, electrical components and film manufacture.&nbsp;</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">For a list of businesses &amp; organisations that may accept X-Rays for recycling, visit PlanetArk at&nbsp;<a href="http://recyclingnearyou.com.au/xray/PenrithNSW">http://recyclingnearyou.com.au/xray/PenrithNSW</a></span></span></p>\n\n<p>&nbsp;&nbsp; &nbsp;&nbsp;</p>\n\n<p>&nbsp;</p>\n', 'b7c2b7976d9a54c60f925a6dcdbc8d87.jpg', 2, '0', '', '', 0, '2015-04-06 13:54:13', '2015-06-08 16:54:25', NULL, NULL, '1'),
(105, 'What goes into the green-lidded organics bin?', '', '', '', 3, '2', '', '', 0, '2015-04-06 15:54:40', '2015-11-16 10:56:19', NULL, NULL, '0'),
(106, 'What items can I recycle? ', '', '', '', 3, '2', '', '', 0, '2015-04-06 15:56:42', '2015-11-24 14:16:37', NULL, NULL, '0'),
(107, 'Aluminium Cans', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Aluminium cans are recyclable - please place them into your recycling bin&nbsp;<br />\nIt is best to squash cans before placing them in the recycling bin to reduce the amount of space they take up.&nbsp;</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Aluminium cans can also be recycled at scrap metal recyclers&nbsp;</span></span></p>\n', 'bfb09e81af2e24108f849ef2c4d70802.jpg', 2, '0', '', '', 0, '2015-04-07 10:34:00', '2015-05-27 12:49:05', NULL, NULL, '0'),
(108, 'Bubble wrap', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Bubble wrap can be disposed of in your red-lidded residual waste bin.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Bubble wrap can also be removed as part of a Council-Clean Up&nbsp;&nbsp;&nbsp;</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">For more information see <a href="https://www.penrithcity.nsw.gov.au/Waste-and-Environment/Waste/On-call-clean-up/">Council Clean-Up Collections</a> or call the Waste Hotline on <a href="tel:1800 734 735">1800 734 735</a></span></span></p>\n', '69e4c69e9d0d1faf465feccf987df881.jpg', 2, '0', '', '', 0, '2015-05-19 13:28:58', '2015-05-27 13:44:13', NULL, NULL, '1'),
(109, 'Compost bins', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Compost bins are a great way to recycle your food and garden scraps as well as&nbsp;create valuable compost to use on your garden.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;"><span style="line-height: 1.6;">Council sells 2 different types of compost bins; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></span></p>\n\n<ul>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">210L Gardenwise compost bin - $35 each (including GST)</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">340L (Large) Gardenwise compost bin - $52 each (including GST)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></li>\n</ul>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Both are 4-sided (each side has an access door), are lidded and come with bolts for assembly.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Note: this is not the green-lidded organics bin (wheely bin) - this is a bin for DIY&nbsp;backyard composting.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;"><strong>The bins are sold from Council&rsquo;s two offices;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\n\n<ul>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">St Marys Office - 207 Queen Street ST MARYS&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Penrith Office - 601 High Street PENRITH</span></span></li>\n</ul>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Many hardware stores, nurseries and some department stores also sell compost&nbsp;bins.</span></span></p>\n', 'a9889f69f9c1ec6c01ae0620133947cd.jpg', 2, '0', '', '', 0, '2015-05-19 13:30:53', '2015-11-02 09:25:22', NULL, NULL, '1'),
(110, 'Fabric', '', '<p><span style="font-family: arial; font-size: 14px; line-height: 22.3999996185303px;">Fabric, if in good condition, can be donated to charities, sold or given away, saving it from landfill.</span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">If in bad condition, fabric can be placed into your red-lidded residual waste bin.</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Fabric can also be boxed or bagged and removed as part of a Council-Clean up.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">For more information see <a href="https://www.penrithcity.nsw.gov.au/Waste-and-Environment/Waste/On-call-clean-up/">Council Clean-Up Collections</a> or call the Waste Hotline on <a href="tel:1800 734 735">1800 734 735</a>. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></span></p>\n', 'bea54c759712ac3768d9551df1bfada9.jpg', 2, '0', '', '', 0, '2015-05-19 13:32:38', '2015-05-27 13:47:18', NULL, NULL, '1'),
(111, 'Glass - drinking', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Glasses, cups, plates and other crockery in bad condition can be placed into the red-lidded residual waste bin. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;"><span style="line-height: 1.6;">These items can also be boxed and removed as part of a Council-Clean Up.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;"><span style="line-height: 1.6;">For more information see <a href="https://www.penrithcity.nsw.gov.au/Waste-and-Environment/Waste/On-call-clean-up/">Council Clean-Up Collections</a> or call the Waste Hotline on <a href="tel:1800 734 735">1800 734 735</a>. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;"><span style="line-height: 1.6;">If in good condition, these items can be donated to charities, sold or given away, saving it from landfill.</span></span></span></p>\n', 'ebbe9e6c60c7a996f5262c91eef662d0.gif', 2, '0', '', '', 0, '2015-05-19 13:43:49', '2015-11-02 09:28:54', NULL, NULL, '1'),
(112, 'Packets (plastic)', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Plastic packets such as chip packets, muesli bar wrappers, lolly bags, frozen vegetable packets and chocolate wrappers can be disposed of in the red-lidded residual waste bin.</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Plastic packets (items made from thin, glad-wrap like plastic) cannot be disposed of in&nbsp;the recycling bin, as it cannot be recycled at the facility.</span></span></p>\n', '593e86a6cce46097be00004cc96ab661.JPG', 2, '0', '', '', 0, '2015-05-19 13:55:34', '2015-05-27 13:50:04', NULL, NULL, '1'),
(113, 'Wrappers (plastic)', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Plastic packets such as chip packets, muesli bar wrappers, lolly bags, frozen vegetable packets and&nbsp;</span></span><span style="font-family: arial; font-size: 14px; line-height: 1.6;">chocolate wrappers can be disposed of in the red-lidded residual waste bin.</span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;"><span style="line-height: 1.6;">Plastic packets (items made from thin, glad-wrap like plastic) cannot be disposed of in&nbsp;the recycling b</span></span></span><span style="font-family: arial; font-size: 14px; line-height: 1.6;">in, as it cannot be recycled at the facility.</span></p>\n', 'e905bc79d9afccb4131596e9ab522908.JPG', 2, '0', '', '', 0, '2015-05-19 13:57:40', '2015-05-27 13:51:06', NULL, NULL, '1'),
(114, 'Bubble Wrap', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Bubble wrap can be disposed of in your red-lidded residual waste bin.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Bubble wrap can also be removed as part of a Council-Clean Up&nbsp;&nbsp;&nbsp;</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">For more information see <a href="https://www.penrithcity.nsw.gov.au/Waste-and-Environment/Waste/On-call-clean-up/">Council Clean-Up Collections</a> or call the Waste Hotline on <a href="tel:1800 734 735">1800 734 735</a></span></span></p>\n', '9f3935ef7c9d316f2e004c1dfea73b12.jpg', 2, '0', '', '', 0, '2015-05-19 14:00:36', '2015-05-27 13:48:35', NULL, NULL, '0'),
(115, 'Compost Bins', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Compost bins are a great way to recycle your food and garden scraps as well as&nbsp;create valuable compost to use on your garden.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;"><span style="line-height: 1.6;">Council sells 2 different types of compost bins; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></span></p>\n\n<ul>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">210L Gardenwise compost bin - $35 each (including GST)</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">340L (Large) Gardenwise compost bin - $52 each (including GST)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></li>\n</ul>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Both are 4-sided (each side has an access door), are lidded and come with bolts for assembly. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Note: this is not the green-lidded organics bin (wheely bin) - this is a bin for DIY&nbsp;backyard composting.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;"><strong>The bins are sold from Council&rsquo;s two offices;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\n\n<ul>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">St Marys Office - 207 Queen Street ST MARYS&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></li>\n	<li><span style="font-size:14px;"><span style="font-family:arial;">Penrith Office - 601 High Street PENRITH</span></span></li>\n</ul>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Many hardware stores, nurseries and some department stores also sell compost&nbsp;bins.</span></span></p>\n', '91f01a64b29ac65c35e69213d89a69b8.jpg', 2, '0', '', '', 0, '2015-05-19 14:02:25', '2015-05-27 13:48:40', NULL, NULL, '0'),
(116, 'Fabric', '', '<p><span style="font-family:arial;"><span style="font-size:14px;">If in bad condition, fabric can be placed into your red-lidded residual waste bin.</span></span></p>\n\n<p><span style="font-family:arial;"><span style="font-size:14px;">Fabric can also be boxed or bagged and removed as part of a Council-Clean up.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\n\n<p><span style="font-family:arial;"><span style="font-size:14px;">For more information see <a href="https://www.penrithcity.nsw.gov.au/Waste-and-Environment/Waste/On-call-clean-up/">Council Clean-Up Collections</a> or call the Waste Hotline on <a href="tel:1800 734 735">1800 734 735</a>.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\n\n<p><span style="font-family:arial;"><span style="font-size:14px;">Fabric, if in good condition, can be donated to charities, sold or given away, saving it from landfill.</span></span></p>\n', 'e4cf925a4d32de24d54eb72ad0a6acb1.jpg', 2, '0', '', '', 0, '2015-05-19 18:56:00', '2015-05-27 13:48:43', NULL, NULL, '0'),
(117, 'Glass - drinking', '', '<p style="line-height: 20.7999992370605px;"><span style="font-size: 14px;"><span style="font-family: arial;">Glasses, cups, plates and other crockery in bad condition can be placed into the red-lidded residual waste bin. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></p>\n\n<p style="line-height: 20.7999992370605px;"><span style="font-size: 14px;"><span style="font-family: arial;"><span style="line-height: 1.6;">These items can also be boxed and removed as part of a Council-Clean Up &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></span></p>\n\n<p style="line-height: 20.7999992370605px;"><span style="font-size: 14px;"><span style="font-family: arial;"><span style="line-height: 1.6;">For more information see <a href="https://www.penrithcity.nsw.gov.au/Waste-and-Environment/Waste/On-call-clean-up/">Council Clean-Up Collections</a> or call the Waste Hotline on <a href="tel:1800 734 735">1800 734 735</a> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></span></span></p>\n\n<p style="line-height: 20.7999992370605px;"><span style="font-size: 14px;"><span style="font-family: arial;"><span style="line-height: 1.6;">If in good condition, these items can be donated to charities, sold or given away, saving it from landfill.</span></span></span></p>\n', '9cde550003f8f1828c469a720a5b0734.jpg', 2, '0', '', '', 0, '2015-05-19 18:57:09', '2015-05-27 13:48:46', NULL, NULL, '0'),
(118, 'Packets (plastic)', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Plastic packets such as chip packets, muesli bar wrappers, lolly bags, frozen vegetable packets and chocolate wrappers can be disposed of in the red-lidded&nbsp;<span style="line-height: 1.6;">residual waste bin. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span></span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;"><span style="line-height: 1.6;">Plastic packets (items made from thin, glad-wrap like plastic) cannot be disposed of in&nbsp;the recycling bin, as it cannot be recycled at the facility.</span></span></span></p>\n', 'ceda1145362c4f0ef25e0066444cb7d3.JPG', 2, '0', '', '', 0, '2015-05-19 18:58:43', '2015-05-27 13:48:49', NULL, NULL, '0'),
(119, 'Wrappers (plastic)', '', '<p><span style="font-size:14px;"><span style="font-family:arial;">Plastic packets such as chip packets, muesli bar wrappers, lolly bags, frozen vegetable packets and</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">chocolate wrappers can be disposed of in the red-lidded residual waste bin.<span style="line-height: 1.6;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Plastic packets (items made from thin, glad-wrap like plastic) cannot be disposed of in&nbsp;the recycling</span></span></p>\n\n<p><span style="font-size:14px;"><span style="font-family:arial;">Bin, as it cannot be recycled at the facility.</span></span></p>\n', '54de1b879785e45c79c362b1b5fce0b8.JPG', 2, '0', '', '', 0, '2015-05-19 19:00:21', '2015-05-27 13:48:51', NULL, NULL, '0'),
