(121, 'Extra Christmas collections', '', '', '', 3, '2', '', '', 0, '2015-05-20 14:36:16', '2016-01-08 09:42:15', NULL, NULL, '0'),
(122, 'Worm farms', '', '<p class="p1"><span style="font-size:14px;"><span style="font-family:arial;"><span class="s1">Worm farms are a great way to recycle your food scraps into valuable liquid fertiliser.&nbsp;</span></span></span></p>\n\n<p class="p1"><span style="font-size:14px;"><span style="font-family:arial;"><span style="line-height: 1.6;">Council sells Reln worm farms for $82 which includes: 1 drainage tray, 2 working trays, bedding block, legs, tap, and lid.&nbsp;</span></span></span></p>\n\n<p class="p2"><span style="font-size:14px;"><span style="font-family:arial;"><b style="line-height: 1.6;">The farms are sold from Council&rsquo;s two offices:</b></span></span></p>\n\n<ul class="ul1">\n	<li class="li1"><span style="font-size:14px;"><span style="font-family:arial;"><span class="s1">Penrith Office - 601 High Street PENRITH</span></span></span></li>\n	<li class="li1"><span style="font-size:14px;"><span style="font-family:arial;"><span class="s1">St Marys Office - 207 Queen Street ST MARYS</span></span></span></li>\n</ul>\n\n<p class="p1"><span style="font-size:14px;"><span style="font-family:arial;"><span class="s1">Many hardware stores, nurseries and some department stores also sell worm farms.</span></span></span></p>\n', 'd75822c70e05e0df3b48a267ac81752b.png', 2, '0', '', '', 0, '2015-05-29 13:53:31', '2015-11-02 09:38:50', NULL, NULL, '1'),
(123, 'Resource Recovery Reminder', '', '<p><span style="font-family:arial;"><span style="font-size: 14px;"><strong>Free text message reminders</strong></span></span></p>\n\n<p><span style="font-family:arial;">To receive a FREE text message reminder for our next Chemical CleanOut Event, Electronic Waste Drop-Off Day and other waste and recycling initiatives, register for Council&#39;s Resource Recovery Reminder.<br />\n<br />\nSend your name, home address and mobile phone number to <a href="mailto:rrr@penrithcity.nsw.gov.au?Subject=Resource%20Recovery%20Reminder" target="_top">rrr@penrithcity.nsw.gov.au</a> or call <a href="tel:%20024732%207615" title="4732 7615">4732 7615</a> to register for this free service.</span></p>\n', '', 1, '0', '', '', 0, '2015-11-02 11:09:29', '2015-11-02 11:13:08', NULL, NULL, '1'),
(124, 'Extra Christmas collections', '', '', '', 3, '2', '', '', 0, '2015-11-16 15:45:15', '2016-01-08 09:42:18', NULL, NULL, '0'),
(125, 'CDs & DVDs', '', '<p>CDs and DVDs can be dropped off for recycling at the following locations:</p>\n\n<ul>\n	<li>Penrith Library</li>\n	<li>St Mary&#39;s Library</li>\n	<li>St Clair Library</li>\n	<li>St Clair Leisure Centre</li>\n</ul>\n', '0f1526c087476fcf030f1688838536e1.JPG', 2, '0', '', '', 0, '2015-11-30 14:36:31', '2015-11-30 09:36:31', NULL, NULL, '1'),
(126, 'Christmas recycling drop-off points', '', '', '', 3, '2', '', '', 0, '2015-12-10 09:31:52', '2016-01-31 20:49:45', NULL, NULL, '0'),
(127, 'Christmas recycling drop-off points', '', '', '', 3, '2', '', '', 0, '2015-12-10 10:15:24', '2016-01-31 20:49:45', NULL, NULL, '0'),
(128, 'Extra Christmas collections', '', '<p><span style="font-family:arial;"><strong>25 December 2015 to 7 January 2016</strong></span></p>\n\n<p><span style="font-family:arial;">To help you with additional waste and recycling at Christmas time, we are providing extra bin collections for two weeks.</span></p>\n\n<p><span style="font-family:arial;">The night before your normal collection day, please place all your bins at the kerbside during the two weeks</span>.</p>\n\n<p><img alt="" src="http://ci.draftserver.com/penrith/assets/uploads/images/Other/Extra%20collections%2015-16.PNG" style="width: 300px; height: 473px;" /></p>\n', '', 1, '0', '', '', 0, '2015-12-14 15:21:45', '2016-01-08 09:43:53', NULL, NULL, '0'),
(129, 'Christmas recycling drop-off points', '', '<p><span style="font-family: arial;"><strong>Weekdays 21 December 2015 to 15 January 2016*&nbsp;</strong></span><br />\n<strong style="line-height: 20.8px;">3 PM</strong><strong style="line-height: 20.8px;"> - </strong><strong style="line-height: 20.8px;">6 PM</strong><strong style="line-height: 20.8px;">&nbsp;</strong><br />\n<em>*Excluding Christmas Eve, New Years Eve &amp; Public Holidays</em></p>\n\n<p style="line-height: 20.8px;"><span style="font-family: arial;">To help you with additional recycling at Christmas time, we are providing two drop-off locations for household recycling.&nbsp;</span></p>\n\n<p style="line-height: 20.8px;"><font face="arial"><strong>Penrith </strong>drop-off point:&nbsp;</font><span style="font-family: arial; line-height: 20.8px;">Outside Penrith Library - 601 High Street, Penrith</span></p>\n\n<p style="line-height: 20.8px;"><span style="font-family: arial; line-height: 20.8px;"><strong>Werrington</strong> drop-off point: Corner of Werrington Road &amp; Water Street, Werrington</span></p>\n\n<p style="line-height: 20.8px;"><strong>Items accepted:</strong></p>\n\n<ul style="line-height: 20.8px;">\n	<li>Glass bottles &amp; jars</li>\n	<li>Plastic bottles, tubs &amp; containers</li>\n	<li>Milk &amp; juice cartons</li>\n	<li>Aluminum, steel &amp; aerosol cans</li>\n	<li>Paper &amp; cardboard</li>\n</ul>\n\n<p><a href="https://www.google.com/maps/d/edit?mid=zM1BA7LDnkoU.kDyFm0ziW3TM&amp;usp=sharing"><img alt="" src="http://ci.draftserver.com/penrith/assets/uploads/images/Other/Recycling%20drop%20off%20locations.PNG" style="width: 100%" /></a></p>\n', '', 1, '0', '', '', 0, '2015-12-14 15:25:41', '2016-01-31 20:49:47', NULL, NULL, '0'),
(130, 'Christmas season waste services', 'Find out about extra waste services to help you', '<p><iframe frameborder="0" height="100%" scrolling="yes" src="https://www.penrithcity.nsw.gov.au/Waste-and-Environment/Waste/Christmas-Collections/" width="100%"></iframe></p>\n', '', 1, '0', '', '', 0, '2015-12-15 09:44:09', '2015-12-15 09:56:36', NULL, NULL, '0'),
(155, 'Final Page', 'this is the final page', '<p>end&nbsp; Set the internal pointer of an array to its last element<br></p><p>The array. This array is passed by reference because it is modified by the function. This means you must pass it a real variable and not a function returning an array because only actual variables may be passed by reference.<br></p>', 'b3892673ea86a3d8c604ea08e40663b7.png', 2, '0', '0', '0', 0, '2016-11-23 00:56:38', '2016-11-23 05:41:38', NULL, NULL, '0');

